using System.Windows;\nusing System.Windows.Input;\n\nnamespace YourNamespace\n{\n    public partial class MainWindow : Window\n    {\n        private bool isDragging;\n        private Point clickPosition;\n\n        public MainWindow()\n        {\n            InitializeComponent();\n        }\n\n        private void Grid_MouseLeftButtonDown(object sender, MouseButtonEventArgs e)\n        {\n            isDragging = true;\n            clickPosition = e.GetPosition(this);\n            MovableGrid.CaptureMouse();\n        }\n\n        private void Grid_MouseMove(object sender, MouseEventArgs e)\n        {\n            if (isDragging)\n            {\n                Point currentPosition = e.GetPosition(this);\n                double offsetX = currentPosition.X - clickPosition.X;\n                double offsetY = currentPosition.Y - clickPosition.Y;\n\n                double newLeft = Canvas.GetLeft(MovableGrid) + offsetX;\n                double newTop = Canvas.GetTop(MovableGrid) + offsetY;\n\n                Canvas.SetLeft(MovableGrid, newLeft);\n                Canvas.SetTop(MovableGrid, newTop);\n\n                clickPosition = currentPosition;\n            }\n        }\n\n        private void Grid_MouseLeftButtonUp(object sender, MouseButtonEventArgs e)\n        {\n            isDragging = false;\n            MovableGrid.ReleaseMouseCapture();\n        }\n    }\n}