using System;\nusing System.Diagnostics;\nusing System.IO;\nusing System.Threading;\n\nclass Program\n{\n    static void Main()\n    {\n        string filePath = "test.txt";\n        \n        // Create and open a file for writing in process A\n        using (FileStream fs = new FileStream(filePath, FileMode.Create, FileAccess.Write, FileShare.None))\n        {\n            // Write to the file\n            StreamWriter writer = new StreamWriter(fs);\n            writer.WriteLine("Hello, World!");\n            writer.Flush();\n\n            // Start process B\n            Process processB = new Process();\n            processB.StartInfo.FileName = "msbuild.exe";\n            processB.StartInfo.Arguments = "/t:Build";\n            processB.Start();\n            \n            // Wait for process B to complete\n            processB.WaitForExit();\n        }\n\n        // Now delete the file after process B has completed\n        if (File.Exists(filePath))\n        {\n            File.Delete(filePath);\n            Console.WriteLine("File deleted successfully.");\n        }\n        else\n        {\n            Console.WriteLine("File not found.");\n        }\n    }\n}