using System;\nusing System.Collections;\nusing System.DirectoryServices;\n\npublic void DisplayProperties(DirectoryEntry directoryEntry)\n{\n    if (directoryEntry != null)\n    {\n        PropertyCollection properties = directoryEntry.Properties;\n        foreach (string propertyName in properties.PropertyNames)\n        {\n            PropertyValueCollection propertyValues = properties[propertyName];\n            foreach (object value in propertyValues)\n            {\n                Console.WriteLine($"Name: {propertyName}, Value: {value}");\n            }\n        }\n    }\n    else\n    {\n        Console.WriteLine("DirectoryEntry is null.");\n    }\n}