public class SecurityHeadersConfig\n{\n    public static void AddSecurityHeaders(GlobalFilterCollection filters)\n    {\n        filters.Add(new XFrameOptionsAttribute());\n        filters.Add(new XContentTypeOptionsAttribute());\n        filters.Add(new XXSSProtectionAttribute());\n        filters.Add(new ContentSecurityPolicyAttribute());\n        filters.Add(new StrictTransportSecurityAttribute());\n    }\n}\n\npublic class XFrameOptionsAttribute : ActionFilterAttribute\n{\n    public override void OnResultExecuted(ResultExecutedContext filterContext)\n    {\n        filterContext.HttpContext.Response.Headers["X-Frame-Options"] = "DENY";\n        base.OnResultExecuted(filterContext);\n    }\n}\n\n// Repeat similar pattern for other headers