using ImageMagick;\nusing System;\n\nclass Program\n{\n    static void Main()\n    {\n        string inputFilePath = @"path\to\input\file.pdf"; // or .eps\n        string outputFilePathJpeg = @"path\to\output\file.jpg";\n        string outputFilePathPng = @"path\to\output\file.png";\n        \n        // Convert to JPEG\n        using (MagickImageCollection images = new MagickImageCollection(inputFilePath))\n        {\n            foreach (MagickImage image in images)\n            {\n                image.Format = MagickFormat.Jpeg;\n                image.Write(outputFilePathJpeg);\n            }\n        }\n\n        // Convert to PNG\n        using (MagickImageCollection images = new MagickImageCollection(inputFilePath))\n        {\n            foreach (MagickImage image in images)\n            {\n                image.Format = MagickFormat.Png;\n                image.Write(outputFilePathPng);\n            }\n        }\n\n        Console.WriteLine("Conversion successful.");\n    }\n}