using Novell.Directory.Ldap;\n   using System;\n\n   class Program\n   {\n       static void Main(string[] args)\n       {\n           string ldapHost = "ldap.example.com";\n           int ldapPort = 636; // SSL port\n           string userDN = "cn=admin,dc=example,dc=com";\n           string userPasswd = "password";\n\n           try\n           {\n               // Creating an LdapConnection instance\n               using (LdapConnection ldapConn = new LdapConnection())\n               {\n                   // Enable SSL\n                   ldapConn.SecureSocketLayer = true;\n\n                   // Connect to the server\n                   ldapConn.Connect(ldapHost, ldapPort);\n\n                   // Bind to the server\n                   ldapConn.Bind(userDN, userPasswd);\n\n                   Console.WriteLine("Connected and bound to the LDAP server successfully.");\n               }\n           }\n           catch (LdapException e)\n           {\n               Console.WriteLine("Error: " + e.LdapErrorMessage);\n           }\n           catch (Exception e)\n           {\n               Console.WriteLine("Error: " + e.Message);\n           }\n       }\n   }