using System;\nusing System.Security.Cryptography;\nusing System.Text;\n\npublic class SecureStorage\n{\n    public static byte[] Encrypt(string data, byte[] entropy = null)\n    {\n        byte[] dataBytes = Encoding.UTF8.GetBytes(data);\n        return ProtectedData.Protect(dataBytes, entropy, DataProtectionScope.CurrentUser);\n    }\n\n    public static string Decrypt(byte[] encryptedData, byte[] entropy = null)\n    {\n        byte[] decryptedData = ProtectedData.Unprotect(encryptedData, entropy, DataProtectionScope.CurrentUser);\n        return Encoding.UTF8.GetString(decryptedData);\n    }\n}\n\n// Example usage\nclass Program\n{\n    static void Main()\n    {\n        string sensitiveData = "Your sensitive data here";\n        byte[] entropy = Encoding.UTF8.GetBytes("Optional entropy");\n\n        byte[] encryptedData = SecureStorage.Encrypt(sensitiveData, entropy);\n        string decryptedData = SecureStorage.Decrypt(encryptedData, entropy);\n\n        Console.WriteLine($"Original: {sensitiveData}");\n        Console.WriteLine($"Decrypted: {decryptedData}");\n    }\n}