[Route("api/questionnaire")]\n    public class QuestionnaireController : ApiController\n    {\n        [HttpPost]\n        [Route("submit")]\n        public IHttpActionResult SubmitAnswers([FromBody] QuestionnaireModel model)\n        {\n            // Process the answers and return the result\n            var result = ProcessAnswers(model);\n            return Ok(result);\n        }\n\n        private string ProcessAnswers(QuestionnaireModel model)\n        {\n            // Logic to process the questionnaire\n            return "Product Suggestion based on answers";\n        }\n    }\n\n    public class QuestionnaireModel\n    {\n        public List<string> Answers { get; set; }\n    }