using System;\nusing System.Linq;\nusing System.Windows.Forms;\n\npublic class YourForm : Form\n{\n    private DataGridView dataGridView1 = new DataGridView();\n    private Label label1 = new Label();\n\n    public YourForm()\n    {\n        // Initialize form components and DataGridView here\n\n        // Example data for DataGridView\n        dataGridView1.Columns.Add("ColumnBoxAge", "Age");\n        dataGridView1.Rows.Add(2);\n        dataGridView1.Rows.Add(4);\n        dataGridView1.Rows.Add(6);\n\n        // Your LINQ query to count rows with ColumnBoxAge > 3 && ColumnBoxAge < 5\n        int variable1 = dataGridView1.Rows.Cast<DataGridViewRow>()\n            .Where(row => !row.IsNewRow) // To exclude the new row placeholder\n            .Count(row => {\n                int age;\n                bool isNumeric = int.TryParse(row.Cells["ColumnBoxAge"].Value?.ToString(), out age);\n                return isNumeric && age > 3 && age < 5;\n            });\n\n        label1.Text = variable1.ToString();\n\n        // Add controls to the form\n        Controls.Add(dataGridView1);\n        Controls.Add(label1);\n    }\n}