using System.ComponentModel;\nusing System.Windows;\n\npublic partial class MainWindow : Window\n{\n    public MainWindow()\n    {\n        InitializeComponent();\n    }\n\n    private void Window_Closing(object sender, CancelEventArgs e)\n    {\n        if (MessageBox.Show("Do you really want to close?", "Confirm", MessageBoxButton.YesNo) == MessageBoxResult.No)\n        {\n            e.Cancel = true;\n            this.Hide();\n        }\n    }\n}