private int[] a = new int[100];\n\n// number of elements in array\nprivate int x;\n\n// Selection Sort Algorithm\npublic void sortArray()\n{\n  int i, j;\n  int min, temp;\n\n  for (i = 0; i < x - 1; i++)\n  {\n    min = i;\n\n    for (j = i + 1; j < x; j++)\n    {\n      if (a[j] < a[min])\n      {\n        min = j;\n      }\n    }\n\n    temp = a[i];\n    a[i] = a[min];\n    a[min] = temp;\n  }\n}