using System;\nusing System.Web;\nusing System.Web.Security;\n\npublic class Global : HttpApplication\n{\n    protected void Application_BeginRequest(Object sender, EventArgs e)\n    {\n        string authParam = HttpContext.Current.Request.QueryString["auth"];\n        \n        if (!string.IsNullOrEmpty(authParam) && authParam == "disable")\n        {\n            // Disable IIS authentication logic here\n            HttpContext.Current.Response.StatusCode = 401; // Unauthorized\n            HttpContext.Current.Response.End();\n        }\n    }\n}