using System;\nusing System.Data.SQLite;\n\nclass Program\n{\n    static void Main()\n    {\n        string connectionString = "Data Source=yourdatabase.db3;Version=3;";\n        \n        // Create a connection\n        using (var connection = new SQLiteConnection(connectionString))\n        {\n            connection.Open();\n            \n            // Create a command\n            using (var command = new SQLiteCommand(connection))\n            {\n                // Example: Create a table\n                command.CommandText = "CREATE TABLE IF NOT EXISTS Users (Id INTEGER PRIMARY KEY, Name TEXT)";\n                command.ExecuteNonQuery();\n\n                // Example: Insert data\n                command.CommandText = "INSERT INTO Users (Name) VALUES ('John Doe')";\n                command.ExecuteNonQuery();\n\n                // Example: Read data\n                command.CommandText = "SELECT * FROM Users";\n                using (SQLiteDataReader reader = command.ExecuteReader())\n                {\n                    while (reader.Read())\n                    {\n                        Console.WriteLine($"Id: {reader["Id"]}, Name: {reader["Name"]}");\n                    }\n                }\n            }\n        }\n    }\n}