protected void Page_Load(object sender, EventArgs e)\n{\n    if (!IsPostBack)\n    {\n        string idParam = Request.QueryString["id"];\n        if (idParam != null && idParam.ToLower() == "no")\n        {\n            foreach (ListViewDataItem item in ProcedureListView.Items)\n            {\n                ListView BenefitListView = (ListView)item.FindControl("BenefitListView");\n                if (BenefitListView != null)\n                {\n                    // Hide the header ID column\n                    PlaceHolder layoutPlaceholder = (PlaceHolder)BenefitListView.FindControl("itemPlaceHolder").Parent;\n                    HtmlTableRow headerRow = (HtmlTableRow)layoutPlaceholder.Controls[0];\n                    headerRow.Cells[0].Visible = false;\n\n                    // Hide the data ID column\n                    foreach (ListViewItem benefitItem in BenefitListView.Items)\n                    {\n                        TableRow dataRow = (TableRow)benefitItem.FindControl("dataID").Parent;\n                        dataRow.Cells[0].Visible = false;\n                    }\n                }\n            }\n        }\n    }\n}