string cmd = string.Empty;\n\n   cmd += @"--location --user username:userpass -C - ";\n   cmd += @"--digest -k https://assoc-datafeeds-na.amazon.com/datafeed/getFeed?filename=";\n   cmd += FeedName + ".gz ";\n   cmd += @"-o " + FeedFileFolder + FeedName + ".gz";\n\n   System.Diagnostics.Process proc;\n   System.Diagnostics.ProcessStartInfo procInfo = new System.Diagnostics.ProcessStartInfo();\n\n   procInfo.UseShellExecute = true;\n   procInfo.WorkingDirectory = "C:";\n   procInfo.FileName = "curl.exe";\n   procInfo.Arguments = cmd;\n   procInfo.Verb = "runas"; // Run as administrator\n\n   try\n   {\n       proc = System.Diagnostics.Process.Start(procInfo);\n       proc.WaitForExit();\n   }\n   catch (System.ComponentModel.Win32Exception e)\n   {\n       // Handle the exception if the user cancels the UAC prompt\n       Console.WriteLine(e.Message);\n   }