public void SaveChanges(YourEntity entity)\n{\n    using (var context = new DataContext(connectionString))\n    {\n        // Get the latest version\n        var latestEntity = GetLatestVersion(entity.ID);\n\n        if (latestEntity != null)\n        {\n            // Increment the version number\n            entity.Version = latestEntity.Version + 1;\n        }\n        else\n        {\n            // This is the first version\n            entity.Version = 1;\n        }\n\n        // Insert the new version of the entity\n        context.GetTable<YourEntity>().InsertOnSubmit(entity);\n        context.SubmitChanges();\n    }\n}