using System;\nusing System.Runtime.InteropServices;\n\nclass ParallelPort\n{\n    [DllImport("inpoutx64.dll", EntryPoint = "Out32")]\n    public static extern void Output(int address, int value);\n\n    [DllImport("inpoutx64.dll", EntryPoint = "Inp32")]\n    public static extern int Input(int address);\n\n    static void Main()\n    {\n        int portAddress = 0x378; // Typical address for LPT1\n        int dataToSend = 0xFF;\n\n        Console.WriteLine("Sending data to parallel port...");\n        Output(portAddress, dataToSend);\n\n        Console.WriteLine("Reading data from parallel port...");\n        int dataReceived = Input(portAddress);\n        Console.WriteLine($"Data received: {dataReceived:X}");\n\n        Console.WriteLine("Operation completed.");\n    }\n}