using System;\nusing System.IO;\n\nclass Program\n{\n    static void Main()\n    {\n        string directoryToWatch = @"C:\Path\To\Your\Directory";\n\n        using (FileSystemWatcher watcher = new FileSystemWatcher())\n        {\n            watcher.Path = directoryToWatch;\n\n            // Watch for changes in LastAccess time\n            watcher.NotifyFilter = NotifyFilters.LastAccess;\n\n            // Add event handlers\n            watcher.Changed += OnChanged;\n\n            // Begin watching\n            watcher.EnableRaisingEvents = true;\n\n            Console.WriteLine($"Monitoring access to files in {directoryToWatch}");\n            Console.WriteLine("Press 'q' to quit the sample.");\n\n            // Keep the program running until the user quits\n            while (Console.Read() != 'q') ;\n        }\n    }\n\n    private static void OnChanged(object source, FileSystemEventArgs e)\n    {\n        // Log the access time\n        string logMessage = $"File: {e.FullPath} was accessed at {DateTime.Now}";\n        Console.WriteLine(logMessage);\n        \n        // Optionally write log to a file\n        File.AppendAllText(@"C:\Path\To\Your\Logfile.txt", logMessage + Environment.NewLine);\n    }\n}