using System.Windows;\nusing System.Windows.Controls;\n\nnamespace SilverlightWebPreview\n{\n    public partial class MainPage : UserControl\n    {\n        public MainPage()\n        {\n            InitializeComponent();\n        }\n\n        private void Button_MouseEnter(object sender, System.Windows.Input.MouseEventArgs e)\n        {\n            Button button = sender as Button;\n\n            if (button != null)\n            {\n                // Set the URL based on the button content or tag\n                string url = string.Empty;\n                if (button.Content.ToString() == "Tab 1")\n                {\n                    url = "http://example.com/page1";\n                }\n                else if (button.Content.ToString() == "Tab 2")\n                {\n                    url = "http://example.com/page2";\n                }\n\n                WebPreview.Navigate(new Uri(url));\n                WebPreview.Visibility = Visibility.Visible;\n            }\n        }\n\n        private void Button_MouseLeave(object sender, System.Windows.Input.MouseEventArgs e)\n        {\n            WebPreview.Visibility = Visibility.Collapsed;\n        }\n    }\n}