using System;\nusing System.IO;\nusing System.Security.Cryptography;\nusing System.Text;\n\npublic class TripleDESEncryption\n{\n    public static string Encrypt(string inputText, byte[] key, byte[] iv)\n    {\n        byte[] clearData = Encoding.UTF8.GetBytes(inputText);\n\n        using (MemoryStream ms = new MemoryStream())\n        {\n            using (TripleDES alg = TripleDES.Create())\n            {\n                alg.Key = key;\n                alg.IV = iv;\n                alg.Mode = CipherMode.CBC;\n                alg.Padding = PaddingMode.PKCS7;\n\n                using (CryptoStream cs = new CryptoStream(ms, alg.CreateEncryptor(), CryptoStreamMode.Write))\n                {\n                    cs.Write(clearData, 0, clearData.Length);\n                    cs.FlushFinalBlock();\n                }\n\n                byte[] cipherBytes = ms.ToArray();\n                return Convert.ToBase64String(cipherBytes);\n            }\n        }\n    }\n\n    public static void Main()\n    {\n        byte[] key = new byte[] { 0x41, 0x42, 0x43, 0x44, 0x45, 0x46, 0x47, 0x48, 0x31, 0x32, 0x33, 0x34, 0x35, 0x36, 0x37, 0x38, 0x41, 0x42, 0x43, 0x44 };\n        byte[] iv = new byte[] { 0x30, 0x30, 0x30, 0x30, 0x30, 0x30, 0x30, 0x30 };\n\n        string encryptedText = Encrypt("YourTextHere", key, iv);\n        Console.WriteLine("Encrypted Text: " + encryptedText);\n    }\n}