using System;\nusing System.Xml.Serialization;\nusing System.IO;\n\nclass Program\n{\n    static void Main()\n    {\n        // Assuming your generated class is named 'GeneratedClass'\n        GeneratedClass obj = new GeneratedClass\n        {\n            // Initialize properties\n        };\n\n        // Serialize to XML\n        XmlSerializer serializer = new XmlSerializer(typeof(GeneratedClass));\n        using (StringWriter writer = new StringWriter())\n        {\n            serializer.Serialize(writer, obj);\n            string xmlOutput = writer.ToString();\n            Console.WriteLine(xmlOutput);\n        }\n\n        // Deserialize from XML\n        string xmlInput = "<GeneratedClass>...</GeneratedClass>"; // Your XML string\n        using (StringReader reader = new StringReader(xmlInput))\n        {\n            GeneratedClass deserializedObj = (GeneratedClass)serializer.Deserialize(reader);\n            // Use deserializedObj\n        }\n    }\n}