protected void btnSubmit_Click(object sender, EventArgs e)\n{\n    int cnt = FindOccurence("DropDownListID");\n    AppendRecords();\n    pnlDisplayData.Visible = false;\n    btnSubmit.Visible = false;\n    resultLabel.Attributes.Add("style", "align=center");\n    resultLabel.Visible = true;\n\n    // Reposition the button dynamically\n    btnSubmit.Style["position"] = "absolute";\n    btnSubmit.Style["top"] = "100px"; // Set the desired top position\n    btnSubmit.Style["left"] = "200px"; // Set the desired left position\n    btnSubmit.Visible = true; // Make sure the button is visible\n}