using System;\n   using System.IO;\n   using System.Web.UI;\n\n   public partial class TemplatePage : Page\n   {\n       protected void Page_Load(object sender, EventArgs e)\n       {\n           string filePath = Server.MapPath("~/template.html");\n           string html = File.ReadAllText(filePath);\n\n           // Replace placeholders with actual content\n           string menuContent = "<ul><li>Home</li><li>About</li><li>Contact</li></ul>";\n           string pageContent = "<h1>Welcome to our website</h1><p>This is the main content.</p>";\n\n           html = html.Replace("[menu]", menuContent);\n           html = html.Replace("[content]", pageContent);\n\n           // Write the merged HTML to the response\n           Response.Write(html);\n       }\n   }