using System;\nusing System.Runtime.Remoting;\n\nnamespace RemotingExample\n{\n    public class Server : MarshalByRefObject\n    {\n        public string GetMessage()\n        {\n            return "Hello from the server!";\n        }\n    }\n\n    class Program\n    {\n        static void Main(string[] args)\n        {\n            Server myInstance = new Server();\n\n            // Associate the instance with a specific URL\n            ObjRef objRef = RemotingServices.Marshal(myInstance, "MyURL");\n\n            Console.WriteLine("Server is running. Press Enter to exit...");\n            Console.ReadLine();\n\n            // Optionally, you can unmarshal when done\n            RemotingServices.Disconnect(myInstance);\n        }\n    }\n}