using System;\nusing System.Collections.Generic;\nusing System.Data;\nusing System.Data.SqlClient;\n\npublic void ExecuteQueryWithList(List<string> listOfWords)\n{\n    // Set up your connection string (update with your actual connection string)\n    string connectionString = "your_connection_string_here";\n\n    // Prepare the DataTable to be used as TVP\n    DataTable dt = new DataTable();\n    dt.Columns.Add("word", typeof(string));\n    foreach (string word in listOfWords)\n    {\n        dt.Rows.Add(word);\n    }\n\n    using (SqlConnection conn = new SqlConnection(connectionString))\n    {\n        conn.Open();\n        using (SqlCommand comm = new SqlCommand("YourStoredProcedureOrQuery", conn))\n        {\n            comm.CommandType = CommandType.StoredProcedure; // or CommandType.Text if you are using a query\n\n            // Add TVP parameter\n            SqlParameter tvpParam = comm.Parameters.AddWithValue("@LISTOFWORDS", dt);\n            tvpParam.SqlDbType = SqlDbType.Structured;\n            tvpParam.TypeName = "StringListType"; // This must match the type name created in SQL Server\n\n            // Execute your command\n            using (SqlDataReader reader = comm.ExecuteReader())\n            {\n                while (reader.Read())\n                {\n                    // Process the results\n                }\n            }\n        }\n    }\n}