XElement currentReport = (from item in callReports.Descendants("callReport")\n                          where (int)item.Element("localId") == myCallreports.LocalId\n                          select item).FirstOrDefault();\n\nif (currentReport != null)\n{\n    currentReport.Element("studio").Value = myCallreports.Studio;\n    currentReport.Element("visitDate").Value = myCallreports.Visitdate.ToShortDateString();\n    // Use XCData to store HTML content\n    currentReport.Element("recomendations").ReplaceNodes(new XCData(myCallreports.Comments));\n    currentReport.Element("reactions").ReplaceNodes(new XCData(myCallreports.Ownerreaction));\n\n    // Save the XML document after modification\n    callReports.Save("your_xml_file_path.xml");\n}