using System;\nusing System.IO;\nusing System.Text;\nusing System.Windows.Forms;\n\npublic class RtfToPlainTextConverter\n{\n    public static void Main()\n    {\n        // Sample RTF content\n        string rtfContent = @"{\rtf1\ansi This is \b bold\b0  and this is \i italic\i0 .}";\n        \n        // Convert RTF to plain text\n        using (MemoryStream rtfStream = new MemoryStream(Encoding.UTF8.GetBytes(rtfContent)))\n        using (MemoryStream plainTextStream = ConvertRtfToPlainTextStream(rtfStream))\n        using (StreamReader reader = new StreamReader(plainTextStream))\n        {\n            string plainText = reader.ReadToEnd();\n            Console.WriteLine(plainText);\n        }\n    }\n\n    public static MemoryStream ConvertRtfToPlainTextStream(Stream rtfStream)\n    {\n        // Create a RichTextBox instance\n        using (RichTextBox rtb = new RichTextBox())\n        {\n            // Load the RTF content into the RichTextBox\n            using (StreamReader reader = new StreamReader(rtfStream))\n            {\n                rtb.Rtf = reader.ReadToEnd();\n            }\n\n            // Extract the plain text\n            string plainText = rtb.Text;\n\n            // Convert the plain text to a MemoryStream\n            MemoryStream plainTextStream = new MemoryStream();\n            using (StreamWriter writer = new StreamWriter(plainTextStream, Encoding.UTF8, 1024, true))\n            {\n                writer.Write(plainText);\n            }\n\n            // Reset the stream position to the beginning\n            plainTextStream.Position = 0;\n\n            return plainTextStream;\n        }\n    }\n}