[CustomSoapExtension]\n    public class MyWebServiceClient : System.Web.Services.Protocols.SoapHttpClientProtocol\n    {\n        [CustomSoapExtension]\n        [System.Web.Services.Protocols.SoapDocumentMethod("http://tempuri.org/MyMethod")]\n        public MyResponseType MyMethod(MyRequestType request)\n        {\n            object[] results = this.Invoke("MyMethod", new object[] { request });\n            return (MyResponseType)results[0];\n        }\n    }