using Gtk;\nusing System;\n\npublic class FileChooserExample\n{\n    public static void Main()\n    {\n        Application.Init();\n\n        FileChooserDialog fileChooser = new FileChooserDialog(\n            "Choose a file",\n            null,\n            FileChooserAction.Open,\n            "Cancel", ResponseType.Cancel,\n            "Open", ResponseType.Accept\n        );\n\n        fileChooser.LocalOnly = false;\n\n        if (fileChooser.Run() == (int)ResponseType.Accept)\n        {\n            string fileUri = fileChooser.Uri;\n            if (IsRemoteUri(fileUri))\n            {\n                Console.WriteLine("Remote URI selected: " + fileUri);\n            }\n            else\n            {\n                Console.WriteLine("Local file selected: " + fileChooser.Filename);\n            }\n        }\n\n        fileChooser.Destroy();\n        Application.Quit();\n    }\n\n    private static bool IsRemoteUri(string uri)\n    {\n        return uri.StartsWith("http://") || uri.StartsWith("https://") || uri.StartsWith("ftp://");\n    }\n}