public class CustomProfile : System.Web.Profile.ProfileBase\n{\n    public string FirstName\n    {\n        get { return (string)this["FirstName"]; }\n        set { this["FirstName"] = value; }\n    }\n\n    public string LastName\n    {\n        get { return (string)this["LastName"]; }\n        set { this["LastName"] = value; }\n    }\n\n    public int Age\n    {\n        get { return (int)this["Age"]; }\n        set { this["Age"] = value; }\n    }\n\n    public static CustomProfile GetUserProfile(string username)\n    {\n        return (CustomProfile)System.Web.Profile.ProfileBase.Create(username);\n    }\n}