using DocumentFormat.OpenXml.Packaging;\nusing DocumentFormat.OpenXml.Wordprocessing;\nusing DocumentFormat.OpenXml.Presentation;\n\npublic string ExtractTextFromDocx(string filepath)\n{\n    StringBuilder text = new StringBuilder();\n    using (WordprocessingDocument doc = WordprocessingDocument.Open(filepath, false))\n    {\n        foreach (var element in doc.MainDocumentPart.Document.Body.Elements<Paragraph>())\n        {\n            text.AppendLine(element.InnerText);\n        }\n    }\n    return text.ToString();\n}\n\npublic string ExtractTextFromPptx(string filepath)\n{\n    StringBuilder text = new StringBuilder();\n    using (PresentationDocument presentationDocument = PresentationDocument.Open(filepath, false))\n    {\n        PresentationPart presentationPart = presentationDocument.PresentationPart;\n        foreach (SlidePart slidePart in presentationPart.SlideParts)\n        {\n            foreach (var element in slidePart.Slide.Elements<A.Paragraph>())\n            {\n                text.AppendLine(element.InnerText);\n            }\n        }\n    }\n    return text.ToString();\n}