using System.Diagnostics;\n\npublic static class Ensures {\n    public static Validation That {\n        get {\n            // Always available validation logic\n            return new Validation();\n        }\n    }\n\n    [Conditional("DEBUG")]\n    public static Validation WhileDebuggingThat {\n        get {\n            // Validation logic only available in debug builds\n            return new Validation();\n        }\n    }\n}\n\npublic class Validation {\n    public Validation IsNotNull(object obj) {\n        Debug.Assert(obj != null, "Object should not be null");\n        return this;\n    }\n\n    public Validation IsInRange(int value, int min, int max) {\n        Debug.Assert(value >= min && value <= max, "Value out of range");\n        return this;\n    }\n\n    public Validation IsPositive(int value) {\n        Debug.Assert(value > 0, "Value should be positive");\n        return this;\n    }\n}