using System;\nusing System.Linq;\nusing System.Text.RegularExpressions;\nusing System.Collections.Generic;\n\npublic class Record\n{\n    public int Id { get; set; }\n    public string Text { get; set; }\n}\n\npublic class Program\n{\n    public static void Main()\n    {\n        List<Record> records = new List<Record>\n        {\n            new Record { Id = 1, Text = "abc abccc dabc" },\n            new Record { Id = 2, Text = "abccc dabc" },\n            new Record { Id = 3, Text = "abccc abc dabc" }\n        };\n\n        var pattern = @"\babc\b";\n        var matchedRecords = records.Where(r => Regex.IsMatch(r.Text, pattern)).ToList();\n\n        foreach (var record in matchedRecords)\n        {\n            Console.WriteLine($"Id: {record.Id}, Text: {record.Text}");\n        }\n    }\n}