public partial class Form1 : Form\n{\n    private BindingList<Product> productBindingList;\n    private MyDataContext db;\n\n    public Form1()\n    {\n        InitializeComponent();\n        db = new MyDataContext();\n        LoadData();\n    }\n\n    private void LoadData()\n    {\n        var query = from p in db.Products select p;\n        productBindingList = new BindingList<Product>(query.ToList());\n        dataGridView1.DataSource = productBindingList;\n    }\n\n    private void dataGridView1_CellEndEdit(object sender, DataGridViewCellEventArgs e)\n    {\n        var editedProduct = productBindingList[e.RowIndex];\n        db.SubmitChanges();\n    }\n}