using System;\nusing System.Collections.Generic;\nusing System.Runtime.InteropServices;\nusing ClearCase;\n\nclass Program\n{\n    static void Main(string[] args)\n    {\n        try\n        {\n            IClearCase cc = new ApplicationClass();\n            CCVOB vob = cc.get_VOB("\\VOB-name");\n            CCView view = cc.get_View("ViewTag");\n\n            // Assuming you have a path to the directory within the view\n            string directoryPath = "M:\\ViewTag\\VOB-name\\path\\to\\directory";\n\n            // Get the directory element\n            CCElement directoryElement = cc.get_Element(directoryPath);\n\n            // List contents\n            ListDirectoryContents(directoryElement);\n        }\n        catch (COMException comEx)\n        {\n            Console.WriteLine("COM Exception: " + comEx.Message);\n        }\n        catch (Exception ex)\n        {\n            Console.WriteLine("Exception: " + ex.Message);\n        }\n    }\n\n    static void ListDirectoryContents(CCElement directoryElement)\n    {\n        if (directoryElement != null && directoryElement.Kind == CCKind.cckDirectory)\n        {\n            foreach (CCElement element in directoryElement.Elements)\n            {\n                Console.WriteLine(element.Name);\n            }\n        }\n        else\n        {\n            Console.WriteLine("The specified path is not a directory or does not exist.");\n        }\n    }\n}