using System;\nusing System.IO;\nusing System.Security.Cryptography;\n\npublic class FileEncryptor\n{\n    public static void EncryptFile(string inputFile, string outputFile, byte[] key, byte[] iv)\n    {\n        using (FileStream fileStream = new FileStream(outputFile, FileMode.Create))\n        {\n            using (Aes aes = Aes.Create())\n            {\n                aes.Key = key;\n                aes.IV = iv;\n\n                using (CryptoStream cryptoStream = new CryptoStream(fileStream, aes.CreateEncryptor(), CryptoStreamMode.Write))\n                {\n                    using (FileStream input = new FileStream(inputFile, FileMode.Open))\n                    {\n                        input.CopyTo(cryptoStream);\n                    }\n                }\n            }\n        }\n    }\n\n    public static void DecryptFile(string inputFile, string outputFile, byte[] key, byte[] iv)\n    {\n        using (FileStream fileStream = new FileStream(outputFile, FileMode.Create))\n        {\n            using (Aes aes = Aes.Create())\n            {\n                aes.Key = key;\n                aes.IV = iv;\n\n                using (CryptoStream cryptoStream = new CryptoStream(fileStream, aes.CreateDecryptor(), CryptoStreamMode.Write))\n                {\n                    using (FileStream input = new FileStream(inputFile, FileMode.Open))\n                    {\n                        input.CopyTo(cryptoStream);\n                    }\n                }\n            }\n        }\n    }\n\n    public static void Main()\n    {\n        // Example usage\n        string inputFilePath = "path/to/input/file";\n        string encryptedFilePath = "path/to/encrypted/file";\n        string decryptedFilePath = "path/to/decrypted/file";\n\n        // Generate a new key and IV for AES\n        using (Aes aes = Aes.Create())\n        {\n            byte[] key = aes.Key;\n            byte[] iv = aes.IV;\n\n            EncryptFile(inputFilePath, encryptedFilePath, key, iv);\n            DecryptFile(encryptedFilePath, decryptedFilePath, key, iv);\n        }\n    }\n}