class Person : IDataErrorInfo\n{\n    private bool hasErrors = false;\n    private string ageError = null;\n\n    public Age Age { get; set; }\n\n    #region IDataErrorInfo Members\n\n    public string Error\n    {\n        get\n        {\n            string error = null;\n            if (hasErrors)\n            {\n                error = "The form contains one or more validation errors";\n            }\n            return error;\n        }\n    }\n\n    public string this[string columnName]\n    {\n        get\n        {\n            return DoValidation(columnName);\n        }\n    }\n\n    #endregion\n\n    private string DoValidation(string columnName)\n    {\n        string error = null;\n        switch (columnName)\n        {\n            case "Age":\n                if (Age == null || Age.Value <= 0)\n                {\n                    hasErrors = true;\n                    error = "Age must be a positive number";\n                    ageError = error;\n                }\n                else\n                {\n                    hasErrors = false;\n                    ageError = null;\n                }\n                break;\n        }\n        return error;\n    }\n}\n\npublic class Age\n{\n    public int Value { get; set; }\n}