// Serialize to JSON\n   using (StreamWriter file = File.CreateText(@"patients.json"))\n   {\n       JsonSerializer serializer = new JsonSerializer();\n       serializer.Serialize(file, patients);\n   }\n\n   // Deserialize from JSON\n   using (StreamReader file = File.OpenText(@"patients.json"))\n   {\n       JsonSerializer serializer = new JsonSerializer();\n       List<Patient> patients = (List<Patient>)serializer.Deserialize(file, typeof(List<Patient>));\n   }