using System;\nusing System.Collections.Generic;\nusing System.Web;\n\npublic class Handler : IHttpHandler, System.Web.SessionState.IRequiresSessionState\n{\n    private static Dictionary<string, string> variableStore = new Dictionary<string, string>();\n\n    public void ProcessRequest(HttpContext context)\n    {\n        string clientId = context.Request.Form["clientId"];\n        string variable = context.Request.Form["variable"];\n\n        if (!string.IsNullOrEmpty(clientId))\n        {\n            if (!string.IsNullOrEmpty(variable))\n            {\n                // Store the variable\n                variableStore[clientId] = variable;\n            }\n            else\n            {\n                // Retrieve the variable\n                if (variableStore.ContainsKey(clientId))\n                {\n                    context.Response.Write(variableStore[clientId]);\n                }\n                else\n                {\n                    context.Response.Write("Variable not found");\n                }\n            }\n        }\n    }\n\n    public bool IsReusable\n    {\n        get { return false; }\n    }\n}