public class WizardController : Controller\n    {\n        public ActionResult Index(string PartNum, int PageId = 1)\n        {\n            // Load the appropriate view model based on PartNum and PageId\n            var model = GetWizardModel(PartNum, PageId);\n            return View(model);\n        }\n\n        [HttpPost]\n        public ActionResult ContinueToPage(string PartNum, int PageId, FormCollection form)\n        {\n            // Validate the current page data\n            if (!ValidatePage(form, PageId))\n            {\n                var model = GetWizardModel(PartNum, PageId);\n                return View("Index", model);\n            }\n\n            // Redirect to the next page if validation succeeds\n            return RedirectToAction("Index", new { PartNum, PageId = PageId + 1 });\n        }\n\n        private bool ValidatePage(FormCollection form, int pageId)\n        {\n            // Implement your validation logic here\n            return true;\n        }\n\n        private object GetWizardModel(string partNum, int pageId)\n        {\n            // Load and return the appropriate view model based on PartNum and PageId\n            return new { PartNum = partNum, PageId = pageId };\n        }\n    }