using System;\nusing System.Reflection;\n\n[AttributeUsage(AttributeTargets.Field | AttributeTargets.Property)]\npublic class ABCAttribute : Attribute\n{\n}\n\npublic class MyForm\n{\n    [ABCAttribute]\n    public System.Windows.Forms.TextBox textBox1;\n\n    public bool HasABCAttribute()\n    {\n        // Get the type of the current class\n        Type type = this.GetType();\n\n        // Get the FieldInfo for textBox1\n        FieldInfo fieldInfo = type.GetField("textBox1", BindingFlags.Public | BindingFlags.Instance);\n\n        if (fieldInfo != null)\n        {\n            // Check if the ABCAttribute is defined on the textBox1 field\n            bool hasAttribute = Attribute.IsDefined(fieldInfo, typeof(ABCAttribute));\n            return hasAttribute;\n        }\n\n        return false;\n    }\n}\n\npublic class Program\n{\n    public static void Main()\n    {\n        MyForm form = new MyForm();\n        bool result = form.HasABCAttribute();\n        Console.WriteLine($"textBox1 has ABCAttribute: {result}");\n    }\n}