private bool ValidatePage(FormCollection form, int pageId)\n    {\n        switch (pageId)\n        {\n            case 1:\n                if (string.IsNullOrEmpty(form["Field1"]))\n                {\n                    ModelState.AddModelError("Field1", "Field 1 is required");\n                    return false;\n                }\n                break;\n            case 2:\n                if (string.IsNullOrEmpty(form["Field2"]))\n                {\n                    ModelState.AddModelError("Field2", "Field 2 is required");\n                    return false;\n                }\n                break;\n            default:\n                break;\n        }\n        return ModelState.IsValid;\n    }