using System;\nusing System.Collections.Generic;\n\npublic class DataSet\n{\n    private List<DataTable> tables;\n\n    public DataSet()\n    {\n        tables = new List<DataTable>();\n    }\n\n    public List<DataTable> Tables => tables;\n\n    public void AddTable(DataTable table)\n    {\n        tables.Add(table);\n    }\n}\n\npublic class DataTable\n{\n    private List<DataRow> rows;\n    private List<DataColumn> columns;\n\n    public DataTable()\n    {\n        rows = new List<DataRow>();\n        columns = new List<DataColumn>();\n    }\n\n    public List<DataRow> Rows => rows;\n    public List<DataColumn> Columns => columns;\n\n    public void AddColumn(DataColumn column)\n    {\n        columns.Add(column);\n    }\n\n    public void AddRow(DataRow row)\n    {\n        rows.Add(row);\n    }\n}\n\npublic class DataColumn\n{\n    public string ColumnName { get; set; }\n    public Type DataType { get; set; }\n}\n\npublic class DataRow\n{\n    private object[] itemArray;\n\n    public DataRow(int columnCount)\n    {\n        itemArray = new object[columnCount];\n    }\n\n    public object this[int columnIndex]\n    {\n        get => itemArray[columnIndex];\n        set => itemArray[columnIndex] = value;\n    }\n}