using System;\nusing System.Diagnostics;\nusing System.Threading;\n\npublic class Program\n{\n    public static void Main()\n    {\n        ProcessStartInfo psInfo = new ProcessStartInfo(@"MyApplication.exe")\n        {\n            RedirectStandardOutput = true,\n            RedirectStandardError = true,\n            UseShellExecute = false,\n            CreateNoWindow = true\n        };\n\n        Process proc = Process.Start(psInfo);\n\n        // Wait for the process to become idle (i.e., the main window is created)\n        proc.WaitForInputIdle();\n\n        // Polling to check if main window handle is set\n        while (proc.MainWindowHandle == IntPtr.Zero)\n        {\n            Thread.Sleep(100); // Wait for a short interval before checking again\n            proc.Refresh(); // Refresh the process information\n        }\n\n        Console.WriteLine("Application is fully loaded.");\n    }\n}