protected void GridView1_RowDataBound(object sender, GridViewRowEventArgs e)\n{\n    if (e.Row.RowType == DataControlRowType.DataRow)\n    {\n        // Assuming the columns are in the order: ACount, BCount, DCount\n        int aCount = Convert.ToInt32(DataBinder.Eval(e.Row.DataItem, "ACount"));\n        int bCount = Convert.ToInt32(DataBinder.Eval(e.Row.DataItem, "BCount"));\n        int dCount = Convert.ToInt32(DataBinder.Eval(e.Row.DataItem, "DCount"));\n\n        Label lblA = (Label)e.Row.FindControl("LabelA");\n        Label lblB = (Label)e.Row.FindControl("LabelB");\n        Label lblD = (Label)e.Row.FindControl("LabelD");\n\n        if (lblA != null)\n        {\n            lblA.Text = aCount < 0 ? "S" : "D";\n        }\n        \n        if (lblB != null)\n        {\n            lblB.Text = bCount < 0 ? "S" : "D";\n        }\n\n        if (lblD != null)\n        {\n            lblD.Text = dCount < 0 ? "S" : "D";\n        }\n    }\n}