using System;\n\npublic class Program\n{\n    public static void Main()\n    {\n        string nom;\n        string ou = String.Empty; // Initialize 'ou' to prevent uninitialized variable issue\n        nom = "1";\n        \n        if (nom == "1")\n        {\n            nom += "1"; // Use "1" as a string to concatenate\n            ou = nom;\n        }\n        \n        Console.Write(ou);\n    }\n}