public class CompanyMapping : ClassMap<Company>\n{\n    public CompanyMapping()\n    {\n        Table("Companies");\n\n        Id(x => x.Identity).Column("Id");\n        Map(x => x.Name).Column("Name");            \n\n        HasMany(x => x.Contacts)\n            .KeyColumn("CompanyID")  // Specify the correct foreign key column name\n            .Inverse()                \n            .LazyLoad()                \n            .Cascade.All();\n    }\n}