using System.Data;\n   using Pervasive.Data.SqlClient;\n\n   string connectionString = "your_connection_string";\n   using (PsqlConnection connection = new PsqlConnection(connectionString))\n   {\n       connection.Open();\n       string query = "SELECT CUSTOMERNBR, NAME FROM CUSTOMER";\n       using (PsqlCommand command = new PsqlCommand(query, connection))\n       {\n           using (PsqlDataReader reader = command.ExecuteReader())\n           {\n               while (reader.Read())\n               {\n                   Console.WriteLine($"{reader["CUSTOMERNBR"]}, {reader["NAME"]}");\n               }\n           }\n       }\n   }