using System;\nusing System.Management;\nusing System.Web.Services;\n\npublic class MyWebService : WebService\n{\n    [WebMethod]\n    public string GetStartTime()\n    {\n        string startTime = string.Empty;\n        ManagementObjectSearcher searcher = new ManagementObjectSearcher("SELECT StartTime FROM Win32_LogonSession");\n        foreach (ManagementObject obj in searcher.Get())\n        {\n            DateTime startDateTime = ManagementDateTimeConverter.ToDateTime(obj["StartTime"].ToString());\n            startTime = startDateTime.ToString("o"); // ISO 8601 format\n            break;\n        }\n        return startTime;\n    }\n}