using System;\nusing System.IO;\nusing System.Threading;\n\npublic class FileMonitor\n{\n    public static void Main(string[] args)\n    {\n        string filePath = @"C:\path\to\your\file.txt";\n\n        if (IsFileReady(filePath))\n        {\n            Console.WriteLine("File is ready for processing.");\n            // Proceed with file processing\n        }\n        else\n        {\n            Console.WriteLine("File is still being copied. Try again later.");\n        }\n    }\n\n    private static bool IsFileReady(string path)\n    {\n        const int maxRetries = 10;\n        const int delayMilliseconds = 500;\n\n        for (int i = 0; i < maxRetries; i++)\n        {\n            try\n            {\n                using (FileStream fileStream = File.Open(path, FileMode.Open, FileAccess.Read, FileShare.None))\n                {\n                    if (fileStream.Length > 0)\n                    {\n                        return true;\n                    }\n                }\n            }\n            catch (IOException)\n            {\n                // File is still being copied or is locked\n                Thread.Sleep(delayMilliseconds); // Wait before retrying\n            }\n        }\n\n        return false; // File is not ready after max retries\n    }\n}